    if (findUser?.stripeSession?.hotelId !== hotelId) {
      return res.status(400).json({
        ...responseObj,
        message: "Please Contact to Customer Support",
      });
    }
    const session = await stripe.checkout.sessions.retrieve(
      findUser?.stripeSession?.id
    );
