    name: {
      type: String,
      trim: true,
      required: true,
    },
    email: {
      type: String,
      trim: true,
      required: true,
      unique: true,
    },
    subTitle: {
      type: String,
      lowercase: true,
    },
    address: {
      type: String,
      trim: true,
    },
    hotelDescprition: {
