  console.log(resss?.url);
  console.log(subtitle)

  const createHotel = await new Hotel({
    name: name,
    email: email,
    address: address,
    price: price,
    phone: phone,
    description: description,
    subtitle: subtitle,
    bannerimage: resss?.url,
    owner:req.user._id
  });
  await createHotel.save();
  res.status(201).json({
    ...responseObj,
    message: "Hotel added successfully",
