import mongoose from "mongoose";
const { Schema } = mongoose;
const { ObjectId } = mongoose.Schema;
const hotelSchema = new Schema(
  {
    name: {
      type: String,
      trim: true,
      required: true,
    },
    email: {
      type: String,
      trim: true,
      required: true,
      unique: true,
    },
    subtitle: {
      type: String,
      lowercase: true,
    },
    address: {
      type: String,
      trim: true,
    },
    description: {
      type: String,
      trim: true,
    },
    bannerimage: {},
    // Feature: [],
    owner: {
      type: ObjectId,
    //   required: true,
      ref: "User",
    },
    feature: [
      {
        type: ObjectId,
        ref: "Feature",
      },
    ],
    price: {
      type: Number,
      default: 5,
    },
    phone: { type: String, min: 10, max: 14 },
  },

  { timestamps: true }
);

export default mongoose.model("Hotel", hotelSchema);

