const { Schema } = mongoose;
const { ObjectId } = mongoose.Schema;
const hotelSchema = new Schema(
  {
    name: {
      type: String,
      trim: true,
      required: true,
    },
    email: {
      type: String,
      trim: true,
      required: true,
      unique: true,
    },
    subTitle: {
      type: String,
      lowercase: true,
    },
    address: {
      type: String,
      trim: true,
