    hotelDescprition: {
      type: String,
      trim: true,
    },
    bannerImage: {},
    // Feature: [],
    feature: {
      type: ObjectId,
      //   required: true,
      ref: "Feature",
    },
    price: {
      type: Number,
      default: 5,
    },
    phone: { type: String, min: 10, max: 14 },
  },

  { timestamps: true }
);

export default mongoose.model("Hotel", hotelSchema);

