import FEATUREaaa from "../Model/FEATURE";
import Hotel from "../Model/HOTEL";
import mongoose, { isValidObjectId } from "mongoose";
import { Types } from "mongoose";

const { ObjectId } = Types;

export const addHotel = async (req, res) => {
  // console.log(req.body);
  const responseObj = {
    response: {},
    message: "Please Enter All Fields",
  };
  const { name, description, email, address, phone, subtitle, price, feature } =
    req.body;
  console.log(feature);
  if (
    !name ||
    !description ||
    !email ||
    !address ||
    !phone ||
    !subtitle ||
    !price ||
    !feature
  ) {
    return res.status(400).json(responseObj);
  }
  const hotelCheck = await Hotel.findOne({ email }).exec();

  if (hotelCheck) {
    return res.status(400).json({
      ...responseObj,
      message: "Hotel with this email address already exists",
    });
  }
  const id = new mongoose.Types.ObjectId();

  const createHotel = new Hotel({
    ...req.body,
    feature: [
      new mongoose.Types.ObjectId("644fced3be7ef130f8487ea7").toString(),
      new mongoose.Types.ObjectId("644fcf1fbe7ef130f8487ead").toString(),
    ],
  });
  await createHotel.save();
  console.log(createHotel);
  //   console.log(hotelCheck);
  res.status(201).json({
    ...responseObj,
    message: "Hotel added successfully",
    response: createHotel,
  });
};

